 p_id=>wwv_flow_imp.id(23244233149590279518)
,p_process_sequence=>10
,p_process_point=>'AFTER_SUBMIT'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'SaveEmp'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'    v_emp_id NUMBER;',
'    v_emp_department_id NUMBER;',
'    v_emp_first_name VARCHAR2(100);',
'    v_emp_last_name VARCHAR2(100);',
'    v_emp_email VARCHAR2(100);',
'    v_emp_hire_date DATE;',
'    v_emp_city VARCHAR2(100);',
'    v_emp_country VARCHAR2(100);',
'    v_emp_job_id NUMBER;',
'    v_row_count NUMBER;',
'',
'    ',
'    CURSOR c_emp IS',
'        SELECT n001,',
'               n002,',
'               n003,',
'               n004,',
'               c001,',
'               c002,',
'               c003,',
'               c004,',
'               c005,',
'               c006,',
'               c007,',
'               d001',
'        FROM apex_collections',
'        WHERE collection_name = ''EMP_COLLECTION'';',
'   ',
'BEGIN',
'',
'    SAVEPOINT before_changes;',
'',
'    ',
'    FOR r IN c_emp LOOP',
'        BEGIN',
'            -- Check exists',
'            SELECT ',
'                id,',
'                department_id, ',
'                first_name, ',
'                last_name, ',
'                email,',
'                hire_date,',
'                city,',
'                country,',
'                job_id',
'            INTO v_emp_id,',
'                 v_emp_department_id, ',
'                 v_emp_first_name, ',
'                 v_emp_last_name, ',
'                 v_emp_email,',
'                 v_emp_hire_date,',
'                 v_emp_city,',
'                 v_emp_country,',
'                 v_emp_job_id',
'            FROM employees',
'            WHERE id = r.n004;',
'            -- check to updated',
'            IF v_emp_department_id != NVL(r.n002,0)',
'                OR v_emp_first_name != r.c003 ',
'                OR v_emp_last_name != r.c004 ',
'                OR v_emp_email != r.c005  ',
'                OR v_emp_hire_date != r.d001  ',
'                OR v_emp_city != r.c006  ',
'                OR v_emp_country != r.c007  ',
'                OR v_emp_job_id != NVL(r.n003,0) THEN',
'                    UPDATE employees',
'                    SET department_id = r.n002,',
'                        first_name = r.c003, ',
'                        last_name = r.c004,',
'                        email = r.c005,  ',
'                        hire_date = r.d001,  ',
'                        city = r.c006,  ',
'                        country = r.c007,  ',
'                        job_id = r.n003',
'                    WHERE id = r.n004;',
'            END IF;',
'        END;',
'',
'    END LOOP;',
'',
'    -- Delete records ',
'    FOR r IN (SELECT id FROM employees emp',
'              WHERE NOT EXISTS (SELECT 1 FROM apex_collections ac',
'                                WHERE ac.collection_name = ''EMP_COLLECTION''',
'                                  AND ac.n004 = emp.id)) LOOP',
'        DELETE FROM employees',
'        WHERE id = r.id;',
'    END LOOP;',
'',
'    COMMIT;',
'',
'EXCEPTION',
'    WHEN OTHERS THEN',
'        APEX_DEBUG.message(p_message => ''EXCEPTION FOUND IN SAVEEMP, ROLLING BACK - '' || SQLERRM);',
'        ROLLBACK TO before_changes;',
'END;'))
,p_process_clob_language=>'PLSQL'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when=>'BT_SAVE'
,p_process_when_type=>'REQUEST_IN_CONDITION'
,p_internal_uid=>23244233149590279518
);
